import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/forum.css";

const Forum = () => {
  const [threads, setThreads] = useState([]);
  const [newThread, setNewThread] = useState({
    title: "",
    content: "",
    author: "",
  });

  useEffect(() => {
    const fetchThreads = async () => {
      try {
        const response = await axios.get("http://localhost:5003/threads");
        setThreads(response.data);
      } catch (error) {
        console.error("Error fetching threads:", error);
      }
    };

    fetchThreads();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewThread((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5003/threads",
        newThread
      );
      console.log(response.data);
      setNewThread({ title: "", content: "", author: "" });
      // Fetch threads again to update the list
      const fetchUpdatedThreads = await axios.get(
        "http://localhost:5003/threads"
      );
      setThreads(fetchUpdatedThreads.data);
    } catch (error) {
      console.error("Error creating thread:", error);
    }
  };

  return (
    <div className="forum">
      <h2>Forum</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          value={newThread.title}
          onChange={handleInputChange}
          placeholder="Title"
          required
        />
        <textarea
          name="content"
          value={newThread.content}
          onChange={handleInputChange}
          placeholder="Content"
          required
        />

        <button type="submit">Create Thread</button>
      </form>
      <div className="threads">
        {threads.map((thread) => (
          <div key={thread.id} className="thread">
            <h3>{thread.title}</h3>
            <p>{thread.content}</p>
            <p>
              <strong>Author:</strong> {thread.author}
            </p>
            <p>
              <strong>Posted on:</strong>{" "}
              {new Date(thread.createdAt).toLocaleString()}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Forum;
