import React, { useState, useEffect } from "react";
import Chess from "chess.js"; // Import Chess from chess.js
import Chessboard from "chessboardjsx"; // Import Chessboard from chessboardjsx

const ChessBot = () => {
  const [game, setGame] = useState(new Chess());
  const [fen, setFen] = useState("start");

  useEffect(() => {
    const makeBotMove = () => {
      if (!game.game_over()) {
        const moves = game.moves();
        const move = moves[Math.floor(Math.random() * moves.length)];
        game.move(move);
        setFen(game.fen());
      }
    };

    if (game.turn() === "b") {
      // Bot's turn, make a move after a short delay
      setTimeout(makeBotMove, 500);
    }
  }, [game]);

  const onDrop = ({ sourceSquare, targetSquare }) => {
    const move = game.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: "q", // always promote to a queen for simplicity
    });

    if (move === null) return; // move is not legal

    setFen(game.fen());
  };

  return (
    <div>
      <h2>Chess Bot</h2>
      <Chessboard position={fen} onDrop={onDrop} transitionDuration={300} />
    </div>
  );
};

export default ChessBot;
