import React, { useState } from "react";
import Chessboard from "chessboardjsx";
import { Chess } from "chess.js";
import NavBar2 from "./NavBar2";
import "../css/chessbot.css";

const ChessBot = () => {
  const [chess] = useState(new Chess());
  const [playerColor, setPlayerColor] = useState("white");
  const [difficulty, setDifficulty] = useState("medium");
  const [fen, setFen] = useState(chess.fen());
  const [showInvalidMovePopup, setShowInvalidMovePopup] = useState(false);
  const [orientation, setOrientation] = useState("white"); // Added state for board orientation
  const [moveHistory, setMoveHistory] = useState([]); // Added state for move history

  const handlePlayerColorChange = (e) => {
    const selectedColor = e.target.value;
    setPlayerColor(selectedColor);
    // If the player selects black, flip the board orientation
    setOrientation(selectedColor === "black" ? "black" : "white");

    // If the player selects black, make the bot's first move
    if (selectedColor === "black") {
      makeBotMove();
    }
  };

  const handleDifficultyChange = (e) => {
    setDifficulty(e.target.value);
  };

  const makeBotMove = () => {
    // Implement bot move logic based on the selected difficulty
    let move;
    switch (difficulty) {
      case "easy":
        move = getRandomMove(chess);
        break;
      case "medium":
        move = getMediumMove(chess);
        break;
      case "hard":
        move = getHardMove(chess);
        break;
      default:
        move = getRandomMove(chess);
    }

    chess.move(move);
    setFen(chess.fen());
    updateMoveHistory();
  };

  const getRandomMove = (chess) => {
    const possibleMoves = chess.moves();
    return possibleMoves[Math.floor(Math.random() * possibleMoves.length)];
  };

  const handleDrop = (sourceSquare, targetSquare, piece) => {
    // Log current FEN string
    console.log("Current FEN:", fen);

    // Validate player move
    const moveResult = chess.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: "q", // promote to queen for simplicity
    });

    // Log the result of the move validation
    console.log("Move result:", moveResult);

    // If move is valid, make bot move
    if (moveResult) {
      setFen(chess.fen());
      updateMoveHistory();
      setTimeout(makeBotMove, 500); // Delay for better visualization
    } else {
      // Show invalid move pop-up
      setShowInvalidMovePopup(true);
      return "snapback"; // Snap back the piece if move is invalid
    }
  };

  const getMediumMove = (chess) => {
    // Prioritize capturing opponent's pieces if available
    const captureMoves = chess.moves({ verbose: true }).filter((move) => {
      return move.captured !== null;
    });
    if (captureMoves.length > 0) {
      return getRandomMoveFromList(captureMoves);
    }

    // If no capturing moves available, make a random move
    return getRandomMove(chess);
  };

  const getHardMove = (chess) => {
    // Prioritize capturing opponent's pieces if available
    const captureMoves = chess.moves({ verbose: true }).filter((move) => {
      return move.captured !== null;
    });
    if (captureMoves.length > 0) {
      return getRandomMoveFromList(captureMoves);
    }

    // If no capturing moves available, prioritize controlling the center
    const centerSquares = ["d4", "e4", "d5", "e5"];
    const centerMoves = chess.moves({ verbose: true }).filter((move) => {
      return centerSquares.includes(move.to);
    });
    if (centerMoves.length > 0) {
      return getRandomMoveFromList(centerMoves);
    }

    // If no center moves available, make a random move
    return getRandomMove(chess);
  };

  const getRandomMoveFromList = (movesList) => {
    return movesList[Math.floor(Math.random() * movesList.length)];
  };

  const handleInvalidMovePopupClose = () => {
    setShowInvalidMovePopup(false);
  };

  const updateMoveHistory = () => {
    const history = chess.history({ verbose: true });
    setMoveHistory(history);
  };

  return (
    <div>
      <NavBar2 />
      <div className="board-container">
        <Chessboard
          position={fen}
          onDrop={(source, target) => handleDrop(source, target)}
        />
      </div>
      <div className="move-history">
        <h3>Move History</h3>
        <ul>
          {moveHistory.map((move, index) => (
            <li key={index}>
              {index % 2 === 0 ? "White" : "Black"}: {move.san}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};
export default ChessBot;
