import React, { useState } from "react";
import Navbar2 from "./NavBar2";
import "../css/MoodTracker.css";

function MoodTracker() {
  const [anger, setAnger] = useState("");
  const [sadness, setSadness] = useState("");
  const [happiness, setHappiness] = useState("");
  const [advice, setAdvice] = useState("");

  const handleFormSubmit = (event) => {
    event.preventDefault();

    // Store mood tracks for the day
    const moodTrack = {
      date: new Date().toLocaleDateString(),
      anger: parseInt(anger),
      sadness: parseInt(sadness),
      happiness: parseInt(happiness),
    };

    // You can implement logic to store the mood track data in a database or state management system
    console.log("Mood tracked successfully!", moodTrack);

    // Provide advice based on mood control capability
    const newAdvice = provideAdvice(moodTrack);

    // Set the advice to state
    setAdvice(newAdvice);

    // Reset form fields
    setAnger("");
    setSadness("");
    setHappiness("");
  };

  // Function to provide advice based on mood control capability
  const provideAdvice = ({ anger, sadness, happiness }) => {
    let advice = "";

    if (anger <= 4) {
      advice += "Great job controlling your anger! Keep up the good work.\n";
    } else {
      advice +=
        "You seem to have high levels of anger. Try practicing deep breathing exercises or meditation to manage it.\n";
    }

    if (sadness <= 4) {
      advice += "You are managing sadness well! Stay positive.\n";
    } else {
      advice +=
        "Feeling sad? Consider talking to someone you trust or engaging in activities that bring you joy.\n";
    }

    if (happiness >= 7) {
      advice +=
        "You have a high level of happiness! Keep doing what makes you happy.\n";
    } else {
      advice +=
        "If you're struggling to find happiness, try focusing on things you're grateful for and doing activities you enjoy.\n";
    }

    if (advice === "") {
      advice = "Keep up the good work! You're doing great!";
    } else {
      advice += "Remember, it's okay to seek help if you need it.";
    }

    return advice;
  };

  return (
    <div>
      <Navbar2 />
      <div className="mood-tracker-container">
        <h2>Mood Tracker</h2>
        <form onSubmit={handleFormSubmit}>
          <div>
            <label htmlFor="anger">Anger Control (1-10):</label>
            <input
              type="number"
              id="anger"
              min="1"
              max="10"
              value={anger}
              onChange={(e) => setAnger(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="sadness">Sadness Control (1-10):</label>
            <input
              type="number"
              id="sadness"
              min="1"
              max="10"
              value={sadness}
              onChange={(e) => setSadness(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="happiness">Happiness (1-10):</label>
            <input
              type="number"
              id="happiness"
              min="1"
              max="10"
              value={happiness}
              onChange={(e) => setHappiness(e.target.value)}
              required
            />
          </div>
          <button type="submit">Submit</button>
        </form>
        {advice && (
          <div className="advice-container">
            <h3>Advice:</h3>
            <p>{advice}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default MoodTracker;
