import React, { useState, useEffect, useCallback } from "react";
import { Chess } from "chess.js";
import Chessboard from "chessboardjsx";

const ChessBot = () => {
  const [game, setGame] = useState(new Chess());
  const [fen, setFen] = useState(game.fen());

  const makeBotMove = useCallback(() => {
    console.log("Checking if game is over:", game.game_over()); // Debugging line
    if (game.game_over()) return;
    if (game.turn() === "b") {
      const moves = game.moves();
      const move = moves[Math.floor(Math.random() * moves.length)];
      game.move(move);
      setFen(game.fen());
    }
  }, [game]);

  useEffect(() => {
    if (game.turn() === "b" && !game.game_over()) {
      setTimeout(makeBotMove, 500);
    }
  }, [game, makeBotMove]);

  const onDrop = ({ sourceSquare, targetSquare }) => {
    const move = game.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: "q", // always promote to a queen for simplicity
    });

    if (move === null) return; // move is not legal

    setFen(game.fen());

    setTimeout(makeBotMove, 500); // Trigger bot move after player's move
  };

  return (
    <div>
      <h2>Chess Bot</h2>
      <Chessboard
        width={400}
        position={fen}
        onDrop={onDrop}
        transitionDuration={300}
      />
    </div>
  );
};

export default ChessBot;
