import React, { useState } from "react";
import ReactDOM from "react-dom";
import Chessboard from "chessboardjsx";
import { Chess } from "chess.js"; // Updated import statement

class ChessBot {
  constructor() {
    this.engineColor = "black"; // Default engine color
    this.engineLevel = 3; // Default engine level (difficulty)
    this.engine = new Chess();
  }

  // Set the color of the engine
  setEngineColor(color) {
    this.engineColor = color;
  }

  // Set the level (difficulty) of the engine
  setEngineLevel(level) {
    this.engineLevel = level;
  }

  // Make a move for the engine
  makeEngineMove() {
    if (this.engine.turn() !== this.engineColor || this.engine.game_over()) {
      return null;
    }

    let legalMoves = this.engine.moves();
    let move = legalMoves[Math.floor(Math.random() * legalMoves.length)];
    this.engine.move(move);
    return move;
  }

  // Make a move for the human player
  makeHumanMove(from, to) {
    console.log("From:", from);
    console.log("To:", to);

    let move = null;
    const promotionRank = this.engineColor === "white" ? 7 : 0;

    // Check if the move results in promotion
    const isPromotionMove = to[1] === promotionRank;

    if (isPromotionMove) {
      // If it's a promotion move, wait for player to specify promotion
      return { from, to, promotion: "q" };
    } else {
      move = this.engine.move({ from: from, to: to });
    }

    return move;
  }

  // Handle promotion for the human player
  handlePromotion(from, to, promotionPiece) {
    return this.engine.move({ from, to, promotion: promotionPiece });
  }

  // Get the current board state
  getBoard() {
    return this.engine.fen();
  }

  // Reset the game
  resetGame() {
    this.engine.reset();
  }
}

const ChessBotApp = () => {
  const [fen, setFen] = useState("start");
  const [gameOver, setGameOver] = useState(false);
  const [playerColor, setPlayerColor] = useState("white");
  const chessBot = new ChessBot();

  // Function to handle when a move is made by the player
  const handleMove = ({ from, to }) => {
    const move = chessBot.makeHumanMove(from, to);
    if (move) {
      const engineMove = chessBot.makeEngineMove();
      if (engineMove) {
        setFen(chessBot.getBoard());
        setGameOver(chessBot.engine.game_over());
      }
    }
  };

  // Function to handle when the player chooses their color
  const chooseColor = (color) => {
    setPlayerColor(color);
    chessBot.setEngineColor(color === "white" ? "black" : "white");
  };

  // Function to start a new game
  const newGame = () => {
    chessBot.resetGame();
    setFen("start");
    setGameOver(false);
  };

  return (
    <div>
      <Chessboard
        width={400}
        position={fen}
        onDrop={handleMove}
        orientation={playerColor}
        dropOffBoard="snapback"
        allowDrag={() => !gameOver}
      />
      <div>
        <button onClick={() => chooseColor("white")}>Play as White</button>
        <button onClick={() => chooseColor("black")}>Play as Black</button>
        <button onClick={newGame}>New Game</button>
      </div>
    </div>
  );
};

export default ChessBotApp;
