import React, { useState, useEffect, useCallback } from "react";
import { Chess } from "chess.js";
import Chessboard from "chessboardjsx";
import "../css/chessbot.css";

const ChessBot = () => {
  const [game, setGame] = useState(new Chess());
  const [fen, setFen] = useState(game.fen());
  const [playerColor, setPlayerColor] = useState("w"); // Default to white
  const [promotionMove, setPromotionMove] = useState(null);
  const [showPromotionModal, setShowPromotionModal] = useState(false);
  const [moveHistory, setMoveHistory] = useState([]);
  const [highlightedSquares, setHighlightedSquares] = useState([]);
  const [timerEnabled, setTimerEnabled] = useState(false);
  const [timer, setTimer] = useState({ minutes: 5, seconds: 0 }); // Initial timer duration in minutes and seconds
  const [timerWhite, setTimerWhite] = useState({ minutes: 5, seconds: 0 }); // Timer for white player
  const [timerBlack, setTimerBlack] = useState({ minutes: 5, seconds: 0 }); // Timer for black player
  const [timerRunning, setTimerRunning] = useState(false);

  useEffect(() => {
    console.log("Game instance:", game);
    console.log("Available methods:", Object.keys(game));
  }, [game]);

  useEffect(() => {
    const interval = setInterval(() => {
      if (timerEnabled && timerRunning && !isGameOver()) {
        if (game.turn() === "w") {
          if (timerWhite.minutes === 0 && timerWhite.seconds === 0) {
            handleTimerEnd();
          } else {
            updateTimer("white");
          }
        } else {
          if (timerBlack.minutes === 0 && timerBlack.seconds === 0) {
            handleTimerEnd();
          } else {
            updateTimer("black");
          }
        }
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [timerEnabled, timerRunning, game, timerWhite, timerBlack]);

  const isGameOver = useCallback(() => {
    return (
      game.isCheckmate() ||
      game.isStalemate() ||
      game.isInsufficientMaterial() ||
      game.isDraw() ||
      game.isThreefoldRepetition()
    );
  }, [game]);

  const makeBotMove = useCallback(() => {
    if (isGameOver()) return;
    if (game.turn() !== playerColor) {
      const moves = game.moves();
      const move = moves[Math.floor(Math.random() * moves.length)];
      game.move(move);
      setFen(game.fen());
      setMoveHistory(game.history({ verbose: true }));
    }
  }, [game, isGameOver, playerColor]);

  useEffect(() => {
    if (game.turn() !== playerColor && !isGameOver()) {
      setTimeout(makeBotMove, 500);
    }
  }, [game, makeBotMove, isGameOver, playerColor]);

  const handlePieceClick = (square) => {
    const moves = game.moves({ square, verbose: true });
    const moveSquares = moves.map((move) => move.to);
    setHighlightedSquares(moveSquares);
  };

  const handlePromotion = (promotion) => {
    const move = promotionMove;
    move.promotion = promotion;
    game.move(move);
    setFen(game.fen());
    setShowPromotionModal(false);
    setPromotionMove(null);

    setTimeout(makeBotMove, 500);
  };

  const onDrop = ({ sourceSquare, targetSquare }) => {
    if (game.turn() !== playerColor) return;

    const move = game.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: "q", // Default promotion, will be overridden by modal
    });

    if (move === null) return;

    if (
      (targetSquare[1] === "8" && sourceSquare[1] === "7") ||
      (targetSquare[1] === "1" && sourceSquare[1] === "2")
    ) {
      game.undo();
      setPromotionMove({ from: sourceSquare, to: targetSquare });
      setShowPromotionModal(true);
    } else {
      setFen(game.fen());
      setMoveHistory(game.history({ verbose: true }));
      setTimeout(makeBotMove, 500);
    }
  };

  const togglePlayerColor = () => {
    setPlayerColor(playerColor === "w" ? "b" : "w");
    const newGame = new Chess();
    setGame(newGame);
    setFen(newGame.fen());
    setMoveHistory([]);
  };

  const toggleTimer = () => {
    setTimerEnabled(!timerEnabled);
    if (!timerEnabled) {
      setTimerRunning(true);
    }
  };

  const startPauseTimer = () => {
    if (timerEnabled) {
      setTimerRunning(!timerRunning);
    }
  };

  const resetTimer = () => {
    setTimer({ minutes: 5, seconds: 0 }); // Reset timer to 5 minutes
    setTimerWhite({ minutes: 5, seconds: 0 });
    setTimerBlack({ minutes: 5, seconds: 0 });
  };

  const updateTimer = (color) => {
    if (color === "white") {
      setTimerWhite((prevTimer) => {
        const updatedSeconds = prevTimer.seconds - 1;
        if (updatedSeconds < 0) {
          const updatedMinutes = prevTimer.minutes - 1;
          if (updatedMinutes < 0) {
            handleTimerEnd();
            return prevTimer;
          }
          return { minutes: updatedMinutes, seconds: 59 };
        }
        return { minutes: prevTimer.minutes, seconds: updatedSeconds };
      });
    } else {
      setTimerBlack((prevTimer) => {
        const updatedSeconds = prevTimer.seconds - 1;
        if (updatedSeconds < 0) {
          const updatedMinutes = prevTimer.minutes - 1;
          if (updatedMinutes < 0) {
            handleTimerEnd();
            return prevTimer;
          }
          return { minutes: updatedMinutes, seconds: 59 };
        }
        return { minutes: prevTimer.minutes, seconds: updatedSeconds };
      });
    }
  };

  const handleTimerEnd = () => {
    setTimerRunning(false);
    // Handle timer end logic here
  };

  const handleTimerChange = (event, field) => {
    const value = parseInt(event.target.value);
    if (isNaN(value)) return;
    if (field === "minutes") {
      setTimer((prevTimer) => ({ ...prevTimer, minutes: value }));
    } else if (field === "seconds") {
      setTimer((prevTimer) => ({ ...prevTimer, seconds: value }));
    }
  };

  return (
    <div className="chess-container">
      <div className="left-panel">
        <h2>Chess Bot</h2>
        <div className="button-container">
          <button onClick={togglePlayerColor}>
            Play as {playerColor === "w" ? "Black" : "White"}
          </button>
          <button onClick={toggleTimer}>
            {timerEnabled ? "Disable Timer" : "Enable Timer"}
          </button>
          {timerEnabled && (
            <div>
              <div>
                <label>Minutes:</label>
                <input
                  type="number"
                  min="0"
                  value={timer.minutes}
                  onChange={(e) => handleTimerChange(e, "minutes")}
                />
              </div>
              <div>
                <label>Seconds:</label>
                <input
                  type="number"
                  min="0"
                  max="59"
                  value={timer.seconds}
                  onChange={(e) => handleTimerChange(e, "seconds")}
                />
              </div>
              <button onClick={startPauseTimer}>
                {timerRunning ? "Pause Timer" : "Start Timer"}
              </button>
              <button onClick={resetTimer}>Reset Timer</button>
              <p>
                Timer White: {timerWhite.minutes}:
                {timerWhite.seconds < 10 ? "0" : ""}
                {timerWhite.seconds}
              </p>
              <p>
                Timer Black: {timerBlack.minutes}:
                {timerBlack.seconds < 10 ? "0" : ""}
                {timerBlack.seconds}
              </p>
            </div>
          )}
        </div>
      </div>
      <div className="board-and-history">
        <Chessboard
          className="chessboard-container"
          position={fen}
          onDrop={onDrop}
          onMouseOverSquare={handlePieceClick}
          onMouseOutSquare={() => setHighlightedSquares([])}
          squareStyles={{
            ...highlightedSquares.reduce((obj, square) => {
              obj[square] = { backgroundColor: "rgba(255, 255, 0, 0.4)" };
              return obj;
            }, {}),
          }}
          transitionDuration={300}
          orientation={playerColor === "w" ? "white" : "black"}
        />
        <div className="move-history">
          <h3>Move History</h3>
          <ul>
            {moveHistory.map((move, index) => (
              <li key={index}>
                {index % 2 === 0 ? "White" : "Black"} - {move.san}
              </li>
            ))}
          </ul>
        </div>
      </div>
      {showPromotionModal && (
        <div className="modal">
          <h3>Choose promotion piece</h3>
          <button onClick={() => handlePromotion("q")}>Queen</button>
          <button onClick={() => handlePromotion("r")}>Rook</button>
          <button onClick={() => handlePromotion("b")}>Bishop</button>
          <button onClick={() => handlePromotion("n")}>Knight</button>
        </div>
      )}
    </div>
  );
};

export default ChessBot;
