// frontend/src/services/api.js
import axios from "axios";

const API_URL = "http://localhost:5002";

export const getBestMove = async (fen, color, maxDepth) => {
  const response = await axios.post(`${API_URL}/get-best-move`, {
    fen,
    color,
    maxDepth,
  });
  return response.data;
};
