import React, { useState, useEffect } from "react";
import Chessboard from "chessboardjsx";
import { Chess } from "chess.js";

const ChessBot = () => {
  const [chess] = useState(new Chess());
  const [fen, setFen] = useState(chess.fen());
  const [history, setHistory] = useState([]);

  useEffect(() => {
    // Load Stockfish from CDN
    const stockfish = new Worker(
      "https://cdn.jsdelivr.net/npm/stockfish/src/stockfish.js"
    );

    // Start Stockfish
    stockfish.postMessage("uci");
    stockfish.postMessage("ucinewgame");

    // Event listener for Stockfish's response
    stockfish.onmessage = (event) => {
      const message = event.data;
      if (message.startsWith("bestmove")) {
        const bestMove = message.split(" ")[1];
        chess.move(bestMove);
        setFen(chess.fen());
        setHistory([...chess.history()]);
      }
    };

    return () => {
      stockfish.terminate(); // Terminate Stockfish when component unmounts
    };
  }, [chess]);

  const handleMove = (move) => {
    if (chess.move(move)) {
      setFen(chess.fen());
      setHistory([...chess.history()]);
      // Let Stockfish make its move
      stockfish.postMessage(`position fen ${chess.fen()}`);
      stockfish.postMessage("go depth 10");
    }
  };

  return (
    <div>
      <Chessboard
        position={fen}
        onDrop={(move) =>
          handleMove({ from: move.sourceSquare, to: move.targetSquare })
        }
      />
      <div>
        <h2>Move History</h2>
        <ul>
          {history.map((move, index) => (
            <li key={index}>{move}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ChessBot;
