import React, { useState } from "react";
import Chessboard from "chessboardjsx";
import { Chess } from "chess.js";
import NavBar2 from "./NavBar2";
import "../css/chessbot.css";

const ChessBot = () => {
  const [chess] = useState(new Chess());
  const [playerColor, setPlayerColor] = useState("white");
  const [difficulty, setDifficulty] = useState("medium");
  const [fen, setFen] = useState(chess.fen());
  const [showInvalidMovePopup, setShowInvalidMovePopup] = useState(false);
  const [orientation, setOrientation] = useState("white");
  const [moveHistory, setMoveHistory] = useState([]);
  const [gameOverMessage, setGameOverMessage] = useState("");
  const [promotionDialog, setPromotionDialog] = useState({
    show: false,
    move: null,
  });
  const [highlightedSquares, setHighlightedSquares] = useState({});

  const handlePlayerColorChange = (e) => {
    const selectedColor = e.target.value;
    setPlayerColor(selectedColor);
    setOrientation(selectedColor === "black" ? "black" : "white");

    if (selectedColor === "black") {
      makeBotMove();
    }
  };

  const handleDifficultyChange = (e) => {
    setDifficulty(e.target.value);
  };

  const makeBotMove = () => {
    if (chess.gameOver()) return;

    let move;
    switch (difficulty) {
      case "easy":
        move = getRandomMove(chess);
        break;
      case "medium":
        move = getMediumMove(chess);
        break;
      case "hard":
        move = getHardMove(chess);
        break;
      default:
        move = getRandomMove(chess);
    }

    if (move) {
      chess.move(move);
      setFen(chess.fen());
      updateMoveHistory();
      checkGameOver();
    }
  };

  const getRandomMove = (chess) => {
    const possibleMoves = chess.moves();
    return possibleMoves.length > 0
      ? possibleMoves[Math.floor(Math.random() * possibleMoves.length)]
      : null;
  };

  const handleDrop = (move) => {
    // Check if the source square and target square are the same
    if (move.sourceSquare === move.targetSquare) return;

    // Validate the move against the current chess position
    const moveResult = chess.move({
      from: move.sourceSquare,
      to: move.targetSquare,
      promotion: "q", // Assume promotion to queen for simplicity
    });

    // Check if the move is invalid
    if (!moveResult) {
      setShowInvalidMovePopup(true);
      return "snapback";
    }

    // Handle pawn promotion
    if (moveResult.flags.includes("p")) {
      setPromotionDialog({ show: true, move: moveResult });
      return;
    }

    // Update the board and move history
    setFen(chess.fen());
    updateMoveHistory();
    checkGameOver();

    // Make bot move after a delay
    setTimeout(makeBotMove, 500);
  };

  const handlePromotion = (piece) => {
    const { move } = promotionDialog;
    chess.undo();
    chess.move({
      from: move.from,
      to: move.to,
      promotion: piece,
    });
    setFen(chess.fen());
    updateMoveHistory();
    checkGameOver();
    setPromotionDialog({ show: false, move: null });
    setTimeout(makeBotMove, 500);
  };

  const getMediumMove = (chess) => {
    const captureMoves = chess
      .moves({ verbose: true })
      .filter((move) => move.captured !== null);
    if (captureMoves.length > 0) {
      return getRandomMoveFromList(captureMoves);
    }
    return getRandomMove(chess);
  };

  const getHardMove = (chess) => {
    const captureMoves = chess
      .moves({ verbose: true })
      .filter((move) => move.captured !== null);
    if (captureMoves.length > 0) {
      return getRandomMoveFromList(captureMoves);
    }

    const centerSquares = ["d4", "e4", "d5", "e5"];
    const centerMoves = chess
      .moves({ verbose: true })
      .filter((move) => centerSquares.includes(move.to));
    if (centerMoves.length > 0) {
      return getRandomMoveFromList(centerMoves);
    }

    return getRandomMove(chess);
  };

  const getRandomMoveFromList = (movesList) => {
    return movesList[Math.floor(Math.random() * movesList.length)];
  };

  const handleInvalidMovePopupClose = () => {
    setShowInvalidMovePopup(false);
  };

  const updateMoveHistory = () => {
    const history = chess.history({ verbose: true });
    setMoveHistory(history);
  };

  const checkGameOver = () => {
    if (chess.isCheckmate()) {
      setGameOverMessage(
        `Checkmate! ${chess.turn() === "w" ? "Black" : "White"} wins.`
      );
    } else if (chess.isStalemate()) {
      setGameOverMessage("Stalemate! It's a draw.");
    } else if (chess.isDraw()) {
      setGameOverMessage("It's a draw.");
    }
  };

  const handleSquareClick = (square) => {
    const moves = chess.moves({ square, verbose: true });

    if (moves.length === 0) {
      setHighlightedSquares({});
      return;
    }

    const squaresToHighlight = {};
    moves.forEach((move) => {
      squaresToHighlight[move.to] = {
        background: "radial-gradient(circle, #fffc00 36%, transparent 40%)",
        borderRadius: "50%",
      };
    });
    squaresToHighlight[square] = {
      background: "rgba(255, 255, 0, 0.4)",
    };
    setHighlightedSquares(squaresToHighlight);
  };

  return (
    <div>
      <NavBar2 />
      <div className="chessbot-container">
        <div className="sidebar">
          <div className="select-box">
            <label>
              Choose player color:
              <select value={playerColor} onChange={handlePlayerColorChange}>
                <option value="white">White</option>
                <option value="black">Black</option>
              </select>
            </label>
            <label>
              Choose difficulty:
              <select value={difficulty} onChange={handleDifficultyChange}>
                <option value="easy">Easy</option>
                <option value="medium">Medium</option>
                <option value="hard">Hard</option>
              </select>
            </label>
          </div>
        </div>
        <div className="board-container">
          <Chessboard
            position={fen}
            orientation={orientation}
            onSquareClick={handleSquareClick}
            onDrop={handleDrop}
            squareStyles={highlightedSquares}
          />
          {showInvalidMovePopup && (
            <div className="invalid-move-popup">
              <div className="popup-content">
                <span className="close" onClick={handleInvalidMovePopupClose}>
                  &times;
                </span>
                <p>Invalid move. Please try again.</p>
              </div>
            </div>
          )}
        </div>
        <div className="move-history">
          <h3>Move History</h3>
          <ul>
            {moveHistory.map((move, index) => (
              <li key={index}>
                {index % 2 === 0 ? "White" : "Black"}: {move.san}
              </li>
            ))}
          </ul>
        </div>
        {gameOverMessage && (
          <div className="game-over-message">
            <p>{gameOverMessage}</p>
          </div>
        )}
        {promotionDialog.show && (
          <div className="promotion-dialog">
            <div className="promotion-dialog-content">
              <h3>Choose Promotion Piece</h3>
              <button onClick={() => handlePromotion("q")}>Queen</button>
              <button onClick={() => handlePromotion("r")}>Rook</button>
              <button onClick={() => handlePromotion("b")}>Bishop</button>
              <button onClick={() => handlePromotion("n")}>Knight</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChessBot;
