import React, { useState } from "react";
import NavBar2 from "./NavBar2.js";
const LearnChess = () => {
  const [isBeginner, setIsBeginner] = useState(true);

  const toggleLevel = () => {
    setIsBeginner(!isBeginner);
  };

  return (
    <div>
      <NavBar2 />
      <h1>Learn Chess</h1>
      <button onClick={toggleLevel}>
        {isBeginner ? "Switch to Intermediate" : "Switch to Beginner"}
      </button>

      {isBeginner ? (
        <div>
          <h2>Beginner</h2>
          <div className="chapter">
            <h3>Introduction to Chess</h3>
            <p>Introduction to chess rules and basic concepts.</p>
            <img src="../images/chess-intro.jpg" alt="Chess Introduction" />
          </div>
          <div className="chapter">
            <h3>Chess Pieces and Their Movements</h3>
            <p>Detailed explanation and diagrams of chess piece movements.</p>
            <img src="../images/piece-movements.jpg" alt="Piece Movements" />
          </div>
          {/* Add more chapters here */}
        </div>
      ) : (
        <div>
          <h2>Intermediate</h2>
          <h3>Chess Openings</h3>
          <ul>
            <li>The King's Pawn Opening</li>
            <li>The Queen's Gambit</li>
            <li>The Sicilian Defense</li>
          </ul>
          <h3>Middle Game Strategies</h3>
          <ul>
            <li>Control of the Center</li>
            <li>Piece Development</li>
            <li>Pawn Structures</li>
          </ul>
          <h3>Endgame Techniques</h3>
          <ul>
            <li>Basic Checkmates</li>
            <li>King and Pawn Endgames</li>
            <li>Rook Endgames</li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default LearnChess;
