import React, { useState, useEffect } from "react";
import { Link, useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import NavBar2 from "./NavBar2";
import "./dashboard.css";

const Dashboard = () => {
  const { username } = useParams();
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [learningProgress, setLearningProgress] = useState(0);
  const [savedItemsCount, setSavedItemsCount] = useState(0);
  const [forumPostsCount, setForumPostsCount] = useState(0);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5003/profile2/${username}`
        );
        setUserData(response.data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUserData();
  }, [username]);

  useEffect(() => {
    // Fetch additional data such as learning progress, saved items count, and forum posts count
    // Simulated data for demonstration purposes
    setLearningProgress(75);
    setSavedItemsCount(5);
    setForumPostsCount(10);
  }, [userData]);

  const handleLogout = () => {
    // Perform logout action
    navigate("/");
  };

  return (
    <div>
      <NavBar2 />
      <div className="container">
        {userData && (
          <div className="dashboard">
            <h2>Welcome back, {userData.fullname}!</h2>
            <div className="summary">
              <div className="summary-item">
                <h3>Learning Progress</h3>
                <p>{learningProgress}% Complete</p>
                <progress value={learningProgress} max="100"></progress>
              </div>
              <div className="summary-item">
                <h3>Saved Items</h3>
                <p>{savedItemsCount} items saved</p>
                <Link to={`/profile2/${username}/shop`}>View Saved Items</Link>
              </div>
              <div className="summary-item">
                <h3>Forum Activity</h3>
                <p>{forumPostsCount} posts</p>
                <Link to={`/profile2/${username}/forum`}>Go to Forum</Link>
              </div>
            </div>
            <div className="actions">
              <button className="button" onClick={handleLogout}>
                Logout
              </button>
              <Link to={`/profile2/${username}/learn`} className="button">
                Start Learning
              </Link>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
