import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import NavBar2 from "./NavBar2";
import "../css/dashboard.css";

const Dashboard = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [learningProgress, setLearningProgress] = useState(0);
  const [savedItemsCount, setSavedItemsCount] = useState(0);
  const [forumPostsCount, setForumPostsCount] = useState(0);
  const [safetyPrecautions, setSafetyPrecautions] = useState({
    helmet: false,
    kneePads: false,
    elbowPads: false,
  });
  const [savedItems, setSavedItems] = useState([]);

  const username = localStorage.getItem("username");

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5003/profile2/${username}`
        );
        const user = response.data.username;
        setUserData(user);
        setLearningProgress(user.learningProgress || 0);
        setSafetyPrecautions(
          user.safetyPrecautions
            ? JSON.parse(user.safetyPrecautions)
            : {
                helmet: false,
                kneePads: false,
                elbowPads: false,
              }
        );
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUserData();
  }, [username]);

  useEffect(() => {
    const fetchSavedItems = async () => {
      try {
        // Fetch saved items from API
        const response = await axios.get(
          `http://localhost:5003/profile2/${username}/savedItems`
        );
        setSavedItems(response.data);
        setSavedItemsCount(response.data.length);
      } catch (error) {
        console.error("Error fetching saved items:", error);
      }
    };

    fetchSavedItems();
  }, [username]);

  const handleLogout = () => {
    localStorage.removeItem("username");
    navigate("/");
  };

  const handleProgressChange = (event) => {
    setLearningProgress(event.target.value);
  };

  const handleCheckboxChange = (event) => {
    setSafetyPrecautions({
      ...safetyPrecautions,
      [event.target.name]: event.target.checked,
    });
  };

  const saveProgress = async () => {
    try {
      const response = await axios.put(
        `http://localhost:5003/profile2/${username}/progress`,
        {
          learningProgress,
          safetyPrecautions,
        }
      );
      console.log("Progress saved successfully:", response.data);
    } catch (error) {
      console.error("Error saving progress:", error);
    }
  };

  return (
    <div>
      <NavBar2 />
      <div className="container">
        {userData && (
          <div className="dashboard">
            <h2>Welcome back, {userData.fullname}!</h2>
            <div className="summary">
              <div className="summary-item">
                <h3>Learning Progress</h3>
                <p>{learningProgress}% Complete</p>
                <input
                  type="range"
                  value={learningProgress}
                  onChange={handleProgressChange}
                  max="100"
                  className="progress-slider"
                />
                <button className="button" onClick={saveProgress}>
                  Save Progress
                </button>
              </div>
              <div className="summary-item">
                <h3>Safety Precautions</h3>
                <div className="checkboxes">
                  <label>
                    <input
                      type="checkbox"
                      name="helmet"
                      checked={safetyPrecautions.helmet}
                      onChange={handleCheckboxChange}
                    />
                    Helmet
                  </label>
                  <label>
                    <input
                      type="checkbox"
                      name="kneePads"
                      checked={safetyPrecautions.kneePads}
                      onChange={handleCheckboxChange}
                    />
                    Knee Pads
                  </label>
                  <label>
                    <input
                      type="checkbox"
                      name="elbowPads"
                      checked={safetyPrecautions.elbowPads}
                      onChange={handleCheckboxChange}
                    />
                    Elbow Pads
                  </label>
                </div>
                <button className="button" onClick={saveProgress}>
                  Save Safety Precautions
                </button>
              </div>

              <div className="summary-item">
                <h3>Saved Items</h3>
                <div className="saved-items">
                  {savedItems.map((item) => (
                    <div key={item.id} className="saved-item">
                      <img src={item.image} alt={item.name} />
                      <h4>{item.name}</h4>
                      <p>${item.price}</p>
                    </div>
                  ))}
                </div>
                <p>Total Saved Items: {savedItemsCount}</p>
              </div>
            </div>
            <div className="actions">
              <button className="button" onClick={handleLogout}>
                Logout
              </button>
              <Link to={`/profile2/${username}/learn`} className="button">
                Start Learning
              </Link>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
