import React, { useState } from "react";
import "./MoodTracker.css"; // Import MoodTracker.css for styling

const MoodTracker = () => {
  const currentDate = new Date();
  const currentYear = currentDate.getFullYear();
  const currentMonth = currentDate.getMonth();

  // Get the number of days in the current month
  const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();

  // Array to store the dates for the current month
  const datesForCurrentMonth = [];

  // Iterate over the days of the month
  for (let i = 1; i <= daysInMonth; i++) {
    // Format the date as "YYYY-MM-DD"
    const formattedDate = `${currentYear}-${currentMonth + 1}-${i}`;
    datesForCurrentMonth.push(formattedDate);
  }

  // Define initial data structure for mood ratings
  const initialMoodData = datesForCurrentMonth.map((date) => ({
    date,
    emotions: { Happy: null, Sad: null, Angry: null, Excited: null },
  }));

  // State to manage mood data
  const [moodData, setMoodData] = useState(initialMoodData);

  // Function to handle mood rating
  const handleMoodRating = (date, emotion, rating) => {
    // Update the mood data with the new rating
    const updatedMoodData = moodData.map((entry) => {
      if (entry.date === date) {
        return {
          ...entry,
          emotions: {
            ...entry.emotions,
            [emotion]: rating,
          },
        };
      }
      return entry;
    });
    setMoodData(updatedMoodData);
  };

  return (
    <div className="mood-tracker-container">
      <h2>Mood Tracker</h2>
      <table className="mood-table">
        <thead>
          <tr>
            <th>Emotion</th>
            {datesForCurrentMonth.map((date) => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {Object.keys(initialMoodData[0].emotions).map((emotion) => (
            <tr key={emotion}>
              <td>{emotion}</td>
              {initialMoodData.map((entry) => (
                <td key={`${entry.date}-${emotion}`}>
                  <button
                    onClick={() => handleMoodRating(entry.date, emotion, 10)}
                  >
                    10
                  </button>
                  {/* Add more rating buttons or input fields as needed */}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MoodTracker;
