import React, { useState } from "react";
import { Link } from "react-router-dom";
import NavBar2 from "./NavBar2.js";

const LearnChess = () => {
  const [isBeginner, setIsBeginner] = useState(true);

  const toggleLevel = () => {
    setIsBeginner(!isBeginner);
  };

  return (
    <div>
      <NavBar2 />
      <div className="learn-chess-container">
        <h1>Learn Chess</h1>
        <button onClick={toggleLevel}>
          {isBeginner ? "Switch to Intermediate" : "Switch to Beginner"}
        </button>

        {isBeginner ? (
          <div className="chapter">
            <h2>Beginner</h2>
            <div className="chapter-section">
              <h3>Chess Board</h3>
              <p>
                Learn about the chessboard layout and how pieces are positioned.
              </p>
              <Link to="profile/:/chess-board">
                <img src="../images/chess-board.jpg" alt="Chess Board Layout" />
              </Link>
            </div>
            <div className="chapter-section">
              <h3>Chess Board</h3>
              <p>
                Learn about the chessboard layout and how pieces are positioned.
              </p>
              <Link to="/chess-board">
                <img src="../images/chess-board.jpg" alt="Chess Board Layout" />
              </Link>
            </div>
          </div>
        ) : (
          <div className="chapter">
            <h2>Intermediate</h2>
            <div className="chapter-section">
              <h3>Chess Tactics</h3>
              <p>
                Introduction to chess tactics and strategies for intermediate
                players.
              </p>
              {/* Add more content for intermediate level */}
            </div>
            <div className="chapter-section">
              <h3>Advanced Openings</h3>
              <p>Detailed analysis of advanced opening variations in chess.</p>
              {/* Add more content for intermediate level */}
            </div>
            {/* Add more chapters for intermediate level */}
          </div>
        )}
      </div>
    </div>
  );
};

export default LearnChess;
