import React, { useState } from "react";
import "../css/Exercise.css";
import NavBar2 from "./NavBar2.js";

const Exercise = () => {
  const emotions = ["Anger", "Anxiety", "Stress", "Sadness", "Happiness"];
  const exercises = {
    Anger: "Exercise for managing anger",
    Anxiety: "Exercise for reducing anxiety",
    Stress: "Exercise for coping with stress",
    Sadness: "Exercise for dealing with sadness",
    Happiness: "Exercise for enhancing happiness",
  };

  // State to manage selected emotion and exercise
  const [selectedEmotion, setSelectedEmotion] = useState(null);

  // Function to handle emotion selection
  const handleEmotionSelection = (emotion) => {
    setSelectedEmotion(emotion);
  };

  return (
    <div>
      <NavBar2 />
      <div className="exercise-container">
        <h2>Emotion Control Exercises</h2>
        <div className="emotion-list">
          {/* Render list of emotions with scroll bar */}
          <div className="emotion-scroll">
            {emotions.map((emotion) => (
              <button
                key={emotion}
                onClick={() => handleEmotionSelection(emotion)}
                className={selectedEmotion === emotion ? "selected" : ""}
              >
                {emotion}
              </button>
            ))}
          </div>
        </div>
        {/* Display exercise for selected emotion */}
        {selectedEmotion && (
          <div className="exercise">
            <h3>{selectedEmotion}</h3>
            <p>{exercises[selectedEmotion]}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Exercise;
